import React, { useState, useEffect } from 'react';

const LikeButton = () => {
  const [count, counter] = useState(0);

  countUp = () => {
    counter(count + 1)
  }

  useEffect(() => {
    document.getElementById('counter').addEventListener('click', countUp)
  });

  return (
    <button id={"counter"}>いいね数：{count}</button>
  )
};

export default LikeButton
